&lt;h1&gt;Bienvenidos | Welcome&lt;/h1&gt;
&lt;p class=&quot;subtitle&quot;&gt;Selecciona tu idioma | Select your language&lt;/p&gt;

&lt;div class=&quot;language-buttons&quot;&gt;
  &lt;a href=&quot;/es/&quot; class=&quot;lang-button lang-es&quot;&gt;
    &lt;span class=&quot;flag&quot;&gt;🇪🇸&lt;/span&gt;
    &lt;span class=&quot;lang-name&quot;&gt;Español&lt;/span&gt;
    &lt;span class=&quot;lang-desc&quot;&gt;Contenido en español&lt;/span&gt;
  &lt;/a&gt;

  &lt;a href=&quot;/en/&quot; class=&quot;lang-button lang-en&quot;&gt;
    &lt;span class=&quot;flag&quot;&gt;🇬🇧&lt;/span&gt;
    &lt;span class=&quot;lang-name&quot;&gt;English&lt;/span&gt;
    &lt;span class=&quot;lang-desc&quot;&gt;Content in English&lt;/span&gt;
  &lt;/a&gt;
&lt;/div&gt;

&lt;p class=&quot;auto-redirect&quot; id=&quot;auto-redirect-message&quot;&gt;&lt;/p&gt;




.language-selector-home {
  min-height: 80vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.selector-container {
  text-align: center;
  max-width: 600px;
  margin: 0 auto;
}

.sole-logo {
  max-width: 200px;
  margin-bottom: 2rem;
}

.language-selector-home h1 {
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: var(--darkgray);
}

.subtitle {
  font-size: 1.2rem;
  color: var(--gray);
  margin-bottom: 3rem;
}

.language-buttons {
  display: flex;
  gap: 2rem;
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: 2rem;
}

.lang-button {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem 3rem;
  background: var(--lightgray);
  border: 2px solid var(--gray);
  border-radius: 12px;
  text-decoration: none;
  transition: all 0.3s ease;
  min-width: 200px;
}

.lang-button:hover {
  background: var(--highlight);
  border-color: var(--secondary);
  transform: translateY(-5px);
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
}

.lang-button .flag {
  font-size: 4rem;
  margin-bottom: 1rem;
}

.lang-button .lang-name {
  font-size: 1.5rem;
  font-weight: bold;
  color: var(--darkgray);
  margin-bottom: 0.5rem;
}

.lang-button .lang-desc {
  font-size: 0.9rem;
  color: var(--gray);
}

.auto-redirect {
  font-size: 0.9rem;
  color: var(--gray);
  font-style: italic;
  min-height: 1.5rem;
}

@media (max-width: 600px) {
  .language-buttons {
    flex-direction: column;
    gap: 1rem;
  }

  .lang-button {
    width: 100%;
  }
}


// Detección automática del idioma del navegador
(function() {
  const userLang = navigator.language || navigator.userLanguage;
  const isSpanish = userLang.toLowerCase().startsWith(&#039;es&#039;);
  const message = document.getElementById(&#039;auto-redirect-message&#039;);

  if (isSpanish) {
    message.textContent = &#039;🌐 Idioma detectado: Español. Haz clic arriba para continuar.&#039;;
  } else {
    message.textContent = &#039;🌐 Language detected: English. Click above to continue.&#039;;
  }
})();
